import React from 'react';
import Navigation from '../Navigation/Navigation';
import Acropolis from './Archaeology/Acropolis/Acropolis';
import AncientAgora from './Archaeology/AncientAgora/AncientAgora';
import CombinedTicket from './Archaeology/CombinedTicket/CombinedTicket';
import HadriansLibrary from './Archaeology/HadriansLibrary/HadriansLibrary';
import Keramikos from './Archaeology/Keramikos/Keramikos';
import Lykeion from './Archaeology/Lykeion/Lykeion';
import Olympieion from './Archaeology/Olympieion/Olympieion';
import RomanAgora from './Archaeology/RomanAgora/RomanAgora';
import './Sightseeing.scss';
import SightseeingNavigation from './SightseeingNavigation/SightseeingNavigation';

interface IContentProps {
    setCurrentPage : (currentPage : Page) => void;
    setCurrentSection : (infoSection : InfoSection) => void;
    currentSection : InfoSection;
}

const Sightseeing : React.FC<IContentProps> = props => {
    let InfoContent : React.FC;
    switch (props.currentSection) {
        case InfoSection.archaeology:
            InfoContent = Arch;
        break;
        case InfoSection.accommodation:
            InfoContent = Accommodation;
        break;
        case InfoSection.food:
            InfoContent = Food;
        break;
    }
      var transportActiveClassname = props.currentSection == InfoSection.transport ? "active" : "";
      var accommodationActiveClassname = props.currentSection == InfoSection.accommodation ? "active" : "";
      var foodActiveClassname = props.currentSection == InfoSection.food ? "active" : "";
    return (
        <div className={`sightseeing content-wrapper`}>
            <Navigation />

            <h1>Sightseeing</h1>
            <SightseeingNavigation title="Archaeological sites" activeClassname={`first ${transportActiveClassname}`} setCurrentSection={() => props.setCurrentSection(InfoSection.transport)}/>
            <SightseeingNavigation title="Museums" activeClassname={accommodationActiveClassname} setCurrentSection={() => props.setCurrentSection(InfoSection.accommodation)}/>
            <SightseeingNavigation title="Neighborhood" activeClassname={foodActiveClassname} setCurrentSection={() => props.setCurrentSection(InfoSection.food)}/>

            <div className={`info-content`}>
                <InfoContent/>
            </div>
        </div>
    );
}

export default Sightseeing;
