import React from 'react';
import './Acropolis.scss';

const Acropolis : React.FC = props => {
    return (
        <section className={`acropolis`}>
            <h2>Acropolis</h2>
            <b>Best time to visit:</b> 8:00 - 10:00 or 18:00 - 20:00<br></br>
            <b>Time spent there:</b> 2h<br></br>
            Do not try to buy a ticket at the booth there, the lines can be huge. If you want a combined ticket, get it at a lesser site or online.<br></br>
            <b>History: </b>The Acropolis is a hill dedicated to the Goddess Athena, protector of the city. The first temples were destroyed by the Persians in 480BC, then rebuilt during Athens' Golden Age in 447BC.
            The statues used to be colored (<a href='https://www.youtube.com/watch?v=nzs4rB1Pp2k' target='_blank'>cool video here</a>). A British guy, Lord Elgin, stole the best pieces in 1801, which are now exposed at the British Museum. The Greeks are still waiting for their return.
        </section>
    );
}

export default Acropolis;
